import {
  Col, Row,
} from 'antd';
import { Button, Card, Space } from 'antd-mobile';
import { toastFail, toastSuccess } from '../../../../utils/toast';

function SignMessageItem({ title, account, msg }) {
  const handleSign = async () => {
    try {
      await okxwallet.tronWeb.trx.signMessage(msg).then((ret) => {
        console.log(ret);
        toastSuccess();
      });
    } catch (error) {
      toastFail();
    }
  };

  return (
    <Col xs={24} lg={6}>
      <Button
        block
        disabled={!account}
        onClick={handleSign}
        style={{ marginBottom: 8 }}
      >
        {title}
      </Button>
    </Col>
  );
}

function SignMessage({ account }) {
  return (
    <Card title="signMessage">
      <Space direction="vertical" style={{ width: '100%' }}>
        <Row gutter={6}>
          <SignMessageItem title="signMessage" account={account} msg="Hello Tron" />
          <SignMessageItem title="signMessage with approval" account={account} msg="" />
          <SignMessageItem title="signMessage with approval" account={account} msg="" />
          <SignMessageItem title="signMessage with approval gray" account={account} msg="" />
        </Row>
      </Space>
    </Card>
  );
}

export default SignMessage;
